var myApp = angular.module('GridApp', ['ui.router']);
myApp
  .config(function($stateProvider, $urlRouterProvider){
    $stateProvider

    .state('home', {
      url: '/',
      templateUrl: './components/home/homeView.html',
      controller: 'homeCtrl'
    })

    .state('page2', {
      url: '/page2',
      templateUrl: './components/page2/page2View.html',
      controller: 'page2Ctrl'
    });
    $urlRouterProvider.otherwise('/');
  });
myApp
  .controller('homeCtrl', function($scope, homeSrvc){
    $scope.greeting = "Hello World";
    $scope.service = homeSrvc.testService;
  });
myApp
  .service('homeSrvc', function(){
    this.testService = 'service working';
  });
myApp
  .controller('page2Ctrl', function($scope){
    $scope.greeting = "Hello World";
  });